    '(ie. "DROP TABLE IF EXISTS")' => '(ex: \\"DROP TABLE IF EXISTS\\")',
    'You can read information on import at:' => 'Você pode ler informações sobre importação em:',
    'http://doc.prestashop.com/display/PS17/Import' => 'http://doc.prestashop.com/display/PS17/Import',
    'https://en.wikipedia.org/wiki/Comma-separated_values' => 'https://pt.wikipedia.org/wiki/Comma-separated_values',
    'Allowed formats: .csv, .xls, .xlsx, .xlst, .ods, .ots' => 'Formatos permitidos: .csv, .xls, .xlsx, .xlst, .ods, .ots',
